import Styled from 'styled-components';

export const Button = Styled.button`

    background-color : ${ ({ bg, color }) => color };
    color : ${ ({ bg, color }) => color };
    width : 100px;
    height : 50px;
    display : inline-block;
    border : 1px solid red;


`
