import Styled from 'styled-components';

export const Button = Styled.button`

    background-color : ${ ({ bg, color }) => bg };
    color : ${ ({ bg, color }) => color };
    width : 100px;
    height : 50px;
    display : inline-block;
    border : 1px solid ${ ({ bg, color }) => bg };
    border-radius : 5px;
    text-align : center;


    &:hover {
        
    }

`
